mapper.select:
SELECT IDX, MENU_ID, TITLE,
NVL(CONT, ' ') AS CONT,
WRITER, TO_CHAR( REGDATE, 'YYYY-MM-DD HH24:MI:SS DAY') REGDATE, READCOUNT, BNUM, LVL, STEP, NREF FROM BOARD WHERE IDX = ${ idx }

mapper.update:
UPDATE   BOARD
 SET     STEP  = STEP + 1         
 WHERE   NREF  = #{ nref  }
  AND    STEP  > #{ step  }

mapper.delete:

UPDATE   BOARD
  SET    DELNUM = 1
  WHERE  IDX    = #{idx}

mapper.update:

UPDATE   BOARD
 SET     READCOUNT = READCOUNT + 1
 WHERE   IDX   =   #{idx}


UPDATE   BOARD
 SET     TITLE = #{ title },
         CONT  = #{ cont  }
 WHERE   IDX   = #{ idx   }
