mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY NREF DESC, STEP ASC) RNUM, IDX, MENU_ID, TITLE, WRITER, READCOUNT, ( SELECT COUNT( FILE_NUM ) FROM FILES F WHERE F.IDX = B.IDX ) FILESCOUNT, TO_CHAR(REGDATE, 'YYYY-MM-DD HH24:MI:SS') REGDATE, BNUM, LVL, STEP, NREF, DELNUM, PARENT
FROM BOARD B
WHERE MENU_ID = #{menu_id}
) T WHERE T.RNUM BETWEEN #{startnum} AND #{endnum}

mapper.update:

UPDATE   BOARD
 SET     STEP    =  STEP + 1
 WHERE   MENU_ID =  #{ menu_id }
  AND    NREF    =  #{ nref } 
  AND    step    >  #{ step }

insert.foreach:
INTO  FILES  VALUES (
     GET_FILENUM()
   , ( SELECT MAX(IDX) FROM BOARD  )
   , #{ file.filename   }
   , #{ file.fileext    }
   , #{ file.sfilename  }
)

INTO  FILES  VALUES (
     GET_FILENUM()
   , #{ idx             }
   , #{ file.filename   }
   , #{ file.fileext    }
   , #{ file.sfilename  }
)

mapper.update:

UPDATE    BOARD
 SET      TITLE  = #{ title }
        , CONT   = #{ cont  }
 WHERE    IDX    = #{ idx   }
